// @flow strict
import React, { useState, useEffect } from "react";

// $FlowIgnore
import fetch from 'isomorphic-unfetch';

// $FlowIgnore
import styled from 'styled-components';

// $FlowIgnore
import Link from 'next/link';

// Components
import { Card } from '../components/Card';
import { Column } from '../components/Column';
import { Grid } from '../components/Grid';

// Flow
type Props = {
	next: string,
	previous: string,
	results: Array<{
		id: number,
		name: string
	}>
} 

// Pokemon
const Pokemon = ({ next, previous, results }: Props) => {
	const [query, setQuery] = useState();

	// useEffect
	useEffect(() => {
		const fetchData = async () => {
			const pokemon = await fetch(query)
				.then(res => res.json());

			console.log(pokemon.results);

			return { next: pokemon.next, previous: pokemon.previous, results: pokemon.results };
		};

		fetchData();
	}, [query]);

	return (
		<React.Fragment>
			<Grid>
				{results.map((pokemon) => (
					<Column key={pokemon.name} span="4">
						<Card>
							<Link href={`/pokemon/${pokemon.name}`} as={`/pokemon/${pokemon.name}`}>
								<a>{pokemon.name}</a>
							</Link>
						</Card>
					</Column>
				))}

				{!!next && <button onClick={() => setQuery(next)}>Next</button>}
				{!!previous && <button onClick={() => setQuery(previous)}>Previous</button>}
			</Grid>
		</React.Fragment>
	)
}

// Props
Pokemon.getInitialProps = async function() {
	const pokemon = await fetch(`https://pokeapi.co/api/v2/pokemon`)
		.then(res => res.json());

	return { next: pokemon.next, previous: pokemon.previous, results: pokemon.results };
}

// Export
export default Pokemon;
