// @flow strict
import React, { useState, useEffect } from "react";

// $FlowIgnore
import fetch from 'isomorphic-unfetch';

// $FlowIgnore
import styled from 'styled-components';

// $FlowIgnore
import Link from 'next/link';

// Components
import { Card } from '../components/Card';
import { Column } from '../components/Column';
import { Grid } from '../components/Grid';

// Flow
type Props = {
	pokemon: {},
	next: string,
	previous: string,
	results: Array<{
		id: number,
		name: string
	}>
} 

// Pokemon
const Pokemon = ({ next, pokemon, previous, results }: Props) => {
	const [nextPage, setNextPage] = useState();
	const [page, setPage] = useState();
	const [previousPage, setPreviousPage] = useState();

	// useEffect
	useEffect((url) => {
		async function fetchPokemon(url) {
			const pokemon = await fetch(url)
				.then(res => res.json());

			!!pokemon.next && setNextPage(pokemon.next);
			!!pokemon.results && setPage(pokemon.results);
			!!pokemon.previous && setPreviousPage(pokemon.previous);

			console.log(pokemon.results);
		}

		fetchPokemon(url);
	});

	console.log(next, previous);

	return (
		<React.Fragment>
			<Grid>
				{!!results && results.map((result) => (
					<Column key={result.name} span="4">
						<Card>
							<Link href={`/pokemon/${result.name}`} as={`/pokemon/${result.name}`}>
								<a>{result.name}</a>
							</Link>
						</Card>
					</Column>
				))}

				{!!next && <button onClick={() => setNextPage(next)}>Next</button>}
				{!!previous && <button onClick={() => setPreviousPage(previous)}>Previous</button>}
			</Grid>
		</React.Fragment>
	)
}

// Props
Pokemon.getInitialProps = async function() {
	const pokemon = await fetch(`https://pokeapi.co/api/v2/pokemon`)
		.then(res => res.json());

	return { next: pokemon.next, pokemon, previous: pokemon.previous, results: pokemon.results };
}

// Export
export default Pokemon;
