// Imports (lower)
import fetch from 'isomorphic-unfetch';

// Imports (upper)
import Link from 'next/link';

// Styles
import Title from '../../components/Title';

// Pokemon
const Pokemon = props => {
	const { pokemon, next_name, previous_name } = props;

	return (
		<>
			<Title color="green" hidden={false}>
				{pokemon.name}
			</Title>

			<Link href="/pokemon/[name]" as={`/pokemon/${previous_name}`}>
				<a>{previous_name}</a>
			</Link>

			{pokemon.sprites.front_default ? <img key={`${pokemon.name}-front`} src={pokemon.sprites.front_default} /> : null}

			{pokemon.sprites.back_default ? <img key={`${pokemon.name}-back`} src={pokemon.sprites.back_default} /> : null}

			<Link href="/pokemon/[name]" as={`/pokemon/${next_name}`}>
				<a>{next_name}</a>
			</Link>
		</>
	)
};

// Props
Pokemon.getInitialProps = async function(context) {
	const { name } = context.query;

	const pokemon = await fetch(`https://pokeapi.co/api/v2/pokemon/${name}`)
		.then(res => res.json());

	const next = fetch(`https://pokeapi.co/api/v2/pokemon/${pokemon.id + 1}`)
		.then(res => res.json());

	const previous = fetch(`https://pokeapi.co/api/v2/pokemon/${pokemon.id - 1}`)
		.then(res => res.json());

	const [n, p] = await Promise.all([next, previous]);

	return { pokemon, next_name: n.name, previous_name: p.name };
}

export default Pokemon;
