// @flow strict

// $FlowIgnore
import styled from 'styled-components';

const Column = styled.div`
	width: ${props => props.span ? `calc(100% / ${props.span})` : "100%" };
	padding-right: 10px;
	padding-left: 10px;
`;

export { Column };
