// @flow strict

// $FlowIgnore
import styled from 'styled-components';

const Column = styled.div`
	width: ${props => props.width ? `calc(100% / ${props.width})` : "100%" };
	padding-right: 10px;
	padding-left: 10px;
`;

export default Column;
