// @flow strict
import React, { useState, useEffect } from "react";

// $FlowIgnore
import fetch from 'isomorphic-unfetch';

// $FlowIgnore
import styled from 'styled-components';

// $FlowIgnore
import Link from 'next/link';

// Components
import { Card } from '../components/Card';
import { Column } from '../components/Column';
import { Grid } from '../components/Grid';

// Pokemon
const Pokemon = () => {
	const [page, setPage] = useState('https://pokeapi.co/api/v2/pokemon');

	// useEffect
	useEffect(() => {
		const pokemon = fetch(`https://pokeapi.co/api/v2/pokemon`)
			.then(res => res.json());

		setPage(pokemon.results);
	}, [page]);

	return (
		<React.Fragment>
			<Grid>
				{!!pokemon.results && pokemon.results.map((pokemon) => (
					<Column key={pokemon.name} span="4">
						<Card>
							<Link href={`/pokemon/${pokemon.name}`} as={`/pokemon/${pokemon.name}`}>
								<a>{pokemon.name}</a>
							</Link>
						</Card>
					</Column>
				))}

				<button onClick={() => setPage('https://pokeapi.co/api/v2/pokemon')}>Load</button>
			</Grid>
		</React.Fragment>
	)
}

// Export
export default Pokemon;
