// Imports (lower)
import fetch from 'isomorphic-unfetch';
import styled from 'styled-components';

// 
import Link from 'next/link';

// Styles
const Title = styled.h1`
	color: red;
`;

// Pokemon
const Pokemon = props => {
	const router = useRouter();

	return (
		<>
			<Title>
				{props.pokemon.name}
			</Title>

			{props.pokemon.sprites.back_default ? <img src={props.pokemon.sprites.back_default} /> : null}

			<Link href="/pokemon/ivysaur">
				<a>Foo</a>
			</Link>
		</>
	);
};

// Props
Pokemon.getInitialProps = async function(context) {
	const { id } = context.query;

	let pokemon = await fetch(`https://pokeapi.co/api/v2/pokemon/${id}`);
	pokemon = await pokemon.json();

	console.log(pokemon);

	return { pokemon };
}

export default Pokemon;
