// @flow strict
import React, { useState, useEffect } from "react";

// $FlowIgnore
import fetch from 'isomorphic-unfetch';

// $FlowIgnore
import styled from 'styled-components';

// $FlowIgnore
import Link from 'next/link';

// Components
import { Card } from '../components/Card';
import { Column } from '../components/Column';
import { Grid } from '../components/Grid';
import { Navigation } from '../components/Navigation';
import { Quantity } from '../components/Quantity';

// Flow
type Props = {
	pokemon: {
		next: string,
		previous: string,
		results: Array<{
			name: string
		}>
	}
} 

// Pokemon
const Pokemon = ({ pokemon }: Props) => {
	const [next, setNext] = useState(pokemon.next);
	const [previous, setPrevious] = useState(pokemon.previous);
	const [query, setQuery] = useState();
	const [results, setResults] = useState(pokemon.results);

	// useEffect
	useEffect(() => {
		const fetchData = async () => {
			const pokemon = await fetch(query)
				.then(res => res.json());

			setNext(pokemon.next);
			setPrevious(pokemon.previous);

			!!pokemon.results && setResults(pokemon.results);
		};

		fetchData();
	}, [query]);

	return (
		<React.Fragment>
			<Grid>
				{!!results && results.map((result) => (
					<Column key={result.name} span="4">
						<Card>
							<Link href={`/pokemon/${result.name}`} as={`/pokemon/${result.name}`}>
								<a>{result.name}</a>
							</Link>
						</Card>
					</Column>
				))}

				<Quantity />

				<Navigation next={next} previous={previous} />
			</Grid>
		</React.Fragment>
	)
}

// Props
Pokemon.getInitialProps = async function() {
	const pokemon = await fetch('https://pokeapi.co/api/v2/pokemon')
		.then(res => res.json());

	return { pokemon };
}

// Export
export default Pokemon;
