// @flow strict
import React, { useState, useEffect } from "react";

// $FlowIgnore
import fetch from 'isomorphic-unfetch';

// $FlowIgnore
import styled from 'styled-components';

// $FlowIgnore
import Link from 'next/link';

// Components
import { Card } from '../components/Card';
import { Column } from '../components/Column';
import { Grid } from '../components/Grid';

// Flow
type Props = {
	next: string,
	previous: string,
	results: Array<{
		id: number,
		name: string
	}>
}

// Pokemon
const Pokemon = ({ next, previous, results }: Props) => {
	const [page, setPage] = useState('https://pokeapi.co/api/v2/pokemon');

	// useEffect
	useEffect(() => {
		fetch(`${url}`)
			.then(res => res.json())
			.then(pokemon => {
				setResults(pokemon.results);
			})
	}, [page]);

	return (
		<React.Fragment>
			<Grid>
				{!!setResults && setResults.map((pokemon) => (
					<Column key={pokemon.name} span="4">
						<Card>
							<Link href={`/pokemon/${pokemon.name}`} as={`/pokemon/${pokemon.name}`}>
								<a>{pokemon.name}</a>
							</Link>
						</Card>
					</Column>
				))}
			</Grid>

			{!!next && <button onClick={() => setPage(next)}>Next</button>}

			{!!previous && <button onClick={() => setPage(previous)}>Previous</button>}
		</React.Fragment>
	)
}

// Props
Pokemon.getInitialProps = async function() {
	const pokemon = await fetch(`https://pokeapi.co/api/v2/pokemon`)
		.then(res => res.json());

	return { next: pokemon.next, previous: pokemon.previous, results: pokemon.results };
}

// Export
export default Pokemon;
