// Imports (lower)
import fetch from 'isomorphic-unfetch';
import styled from 'styled-components';

// 
import Link from 'next/link';

// Styles
const Title = styled.h1`
	color: red;
`;

// Pokemon
const Pokemon = props => {
	const { pokemon, next_name, previous_name } = props;

	return (
		<>
			<Title>
				{pokemon.name}
			</Title>

			<Link href="/pokemon/[name]" as={`/pokemon/${previous_name}`}>
				<a>{previous_name}</a>
			</Link>

			{pokemon.sprites.back_default ? <img key={pokemon.id} src={pokemon.sprites.back_default} /> : null}

			<Link href="/pokemon/[name]" as={`/pokemon/${next_name}`}>
				<a>{next_name}</a>
			</Link>


		</>
	)
};

// Props
Pokemon.getInitialProps = async function(context) {
	const { name } = context.query;

	let pokemon = await fetch(`https://pokeapi.co/api/v2/pokemon/${name}`);
	pokemon = await pokemon.json();

	let next = await fetch(`https://pokeapi.co/api/v2/pokemon/${pokemon.id + 1}`);
	next = await next.json();

	// let previous = await fetch(`https://pokeapi.co/api/v2/pokemon/${pokemon.id - 1}`);
	// previous = await previous.json();

	console.log(pokemon.id);

	return { pokemon, next_name: next.name, previous_name: previous.name };
}

export default Pokemon;
