// @flow strict

// $FlowIgnore
import fetch from 'isomorphic-unfetch';

// $FlowIgnore
import styled from 'styled-components';

// $FlowIgnore
import Card from '../components/Card';

// $FlowIgnore
import Link from 'next/link';

// Flow
type Props = {
	pokemon: {
		results: Array<{
			name: string
		}>
	}
} 

// Pokemon
const Pokemon = ({ pokemon_results }: Props) => (
	<React.Fragment>
		{pokemon_results.map((pokemon) => (
			<Card>
				<Link href={`/pokemon/${pokemon.name}`} as={`/pokemon/${pokemon.name}`}>
					<a>{pokemon.name}</a>
				</Link>
			</Card>
		))}
	</React.Fragment>
)

// Props
Pokemon.getInitialProps = async function() {
	const pokemon = await fetch(`https://pokeapi.co/api/v2/pokemon`)
		.then(res => res.json());

	return { pokemon, pokemon_results: pokemon.results };
}

export default Pokemon;
