// @flow strict
import React, { useState, useEffect } from "react";

// $FlowIgnore
import fetch from 'isomorphic-unfetch';

// $FlowIgnore
import styled from 'styled-components';

// $FlowIgnore
import Link from 'next/link';

// Components
import { Card } from '../components/Card';
import { Column } from '../components/Column';
import { Grid } from '../components/Grid';

// Pokemon
const Pokemon = () => {
	const [pokemon, setPokemon] = useState({ results: [] });
	const [query, setQuery] = useState('https://pokeapi.co/api/v2/pokemon');

	// useEffect
	useEffect(() => {
		const fetchData = async () => {
			const pokemon = await fetch(query)
				.then(res => res.json());

			console.log(pokemon.results);

			setPokemon(pokemon.results);
		};

		fetchData();
	}, []);

	return (
		<React.Fragment>
			<Grid>
				{pokemon.results.map((pokemon) => (
					<Column key={pokemon.name} span="4">
						<Card>
							<Link href={`/pokemon/${pokemon.name}`} as={`/pokemon/${pokemon.name}`}>
								<a>{pokemon.name}</a>
							</Link>
						</Card>
					</Column>
				))}
			</Grid>
		</React.Fragment>
	)
}

// Export
export default Pokemon;
