// @flow strict
import React, { useState, useEffect } from "react";

// $FlowIgnore
import fetch from 'isomorphic-unfetch';

// $FlowIgnore
import styled from 'styled-components';

// $FlowIgnore
import Link from 'next/link';

// Components
import { Card } from '../components/Card';
import { Column } from '../components/Column';
import { Grid } from '../components/Grid';

// Pokemon
const Pokemon = () => {
	const [next, setNext] = useState();
	const [page, setPage] = useState();
	const [previous, setPrevious] = useState();

	// useEffect
	useEffect((url) => {
		async function fetchPokemon(url = 'https://pokeapi.co/api/v2/pokemon') {
			const pokemon = await fetch(url)
				.then(res => res.json());

			!!pokemon.next && setNext(pokemon.next);
			!!pokemon.results && setPage(pokemon.results);
			!!pokemon.previous && setPrevious(pokemon.previous);

			console.log(pokemon.next);
			console.log(pokemon.results);
			console.log(pokemon.previous);
		}

		fetchPokemon(url);
	}, [page]);

	return (
		<React.Fragment>
			<Grid>
				{!!page && page.map((pokemon) => (
					<Column key={pokemon.name} span="4">
						<Card>
							<Link href={`/pokemon/${pokemon.name}`} as={`/pokemon/${pokemon.name}`}>
								<a>{pokemon.name}</a>
							</Link>
						</Card>
					</Column>
				))}

				{!!next && <button onClick={() => setPage(next)}>Next</button>}
				{!!previous && <button onClick={() => setPage(previous)}>Previous</button>}
			</Grid>
		</React.Fragment>
	)
}

// Export
export default Pokemon;
