// @flow strict

// $FlowIgnore
import fetch from 'isomorphic-unfetch';

// $FlowIgnore
import styled from 'styled-components';

// $FlowIgnore
import Link from 'next/link';

// Components
import { Card } from '../components/Card';
import { Column } from '../components/Column';
import { Grid } from '../components/Grid';

// Flow
type Props = {
	pokemon: {
		results: Array<{
			id: number,
			name: string
		}>
	}
} 

// Pokemon
const Pokemon = ({ results }: Props) => {
	return (
		<React.Fragment>
			<Grid>
				{!!pokemon_results && pokemon_results.map((pokemon) => (
					<Column key={pokemon.name} span="4">
						<Card>
							<Link href={`/pokemon/${pokemon.name}`} as={`/pokemon/${pokemon.name}`}>
								<a>{pokemon.name}</a>
							</Link>
						</Card>
					</Column>
				))}
			</Grid>

			{!!next && <button>Next</button>}

			{!!previous && <button>Next</button>}
		</React.Fragment>
	)
}

// Props
Pokemon.getInitialProps = async function() {
	const pokemon = await fetch(`https://pokeapi.co/api/v2/pokemon`)
		.then(res => res.json());

	return { pokemon, results: pokemon.results };
}

// Export
export default Pokemon;
