// @flow strict
import React, { useState, useEffect } from "react";

// $FlowIgnore
import fetch from 'isomorphic-unfetch';

// $FlowIgnore
import styled from 'styled-components';

// $FlowIgnore
import Link from 'next/link';

// Components
import { Card } from '../components/Card';
import { Column } from '../components/Column';
import { Grid } from '../components/Grid';

// Flow
type Props = {
	pokemon: {}
} 

// Pokemon
const Pokemon = ({ pokemon }: Props) => {
	const [data, setData] = useState(pokemon.results);
	const [next, setNext] = useState(pokemon.next);
	const [previous, setPrevious] = useState();
	const [query, setQuery] = useState();

	// useEffect
	useEffect(() => {
		const fetchData = async () => {
			const pokemon = await fetch(query)
				.then(res => res.json());

			!!pokemon && setData(pokemon.results);
			!!pokemon.next && setNext(pokemon.next);
			!!pokemon.previous && setPrevious(pokemon.previous);
		};

		fetchData();
	}, [query]);

	return (
		<React.Fragment>
			<Grid>
				{!!data && data.map((pokemon) => (
					<Column key={pokemon.name} span="4">
						<Card>
							<Link href={`/pokemon/${pokemon.name}`} as={`/pokemon/${pokemon.name}`}>
								<a>{pokemon.name}</a>
							</Link>
						</Card>
					</Column>
				))}

				{!!next && <button onClick={() => setQuery(next)}>Next</button>}
				{!!previous && <button onClick={() => setQuery(previous)}>Previous</button>}
			</Grid>
		</React.Fragment>
	)
}

// Props
Pokemon.getInitialProps = async function() {
	const pokemon = await fetch(`https://pokeapi.co/api/v2/pokemon`)
		.then(res => res.json());

	return { pokemon };
}

// Export
export default Pokemon;
