// @flow strict

// $FlowIgnore
import fetch from 'isomorphic-unfetch';

// $FlowIgnore
import styled from 'styled-components';

// $FlowIgnore
import Link from 'next/link';

// Components
import Card from '../components/Card';
import Column from '../components/Column';
import Grid from '../components/Grid';

// Flow
type Props = {
	pokemon: {
		results: Array<{
			name: string
		}>
	}
} 

// Pokemon
const Pokemon = ({ pokemon_results }: Props) => (
	<React.Fragment>
		<Grid>
			{pokemon_results.map((pokemon) => (
				<Column width="4">
					<Card>
						<Link href={`/pokemon/${pokemon.name}`} as={`/pokemon/${pokemon.name}`}>
							<a>{pokemon.name}</a>
						</Link>
					</Card>
				</Column>
			))}
		</Grid>
	</React.Fragment>
)

// Props
Pokemon.getInitialProps = async function() {
	const pokemon = await fetch(`https://pokeapi.co/api/v2/pokemon`)
		.then(res => res.json());

	return { pokemon, pokemon_results: pokemon.results };
}

export default Pokemon;
