// Imports (lower)
import fetch from 'isomorphic-unfetch';
import styled from 'styled-components';

// 
import Link from 'next/link';

// Styles
const Title = styled.h1`
	color: red;
`;

// Pokemon
const Pokemon = props => (
	<>
		<Title>
			{props.pokemon.name}
		</Title>

		{props.pokemon.sprites.back_default ? <img src={props.pokemon.sprites.back_default} /> : null}

		<Link href="/pokemon/[name]" as={`/pokemon/${props.next_name}`}>
			<a>Foo</a>
		</Link>
	</>
);

// Props
Pokemon.getInitialProps = async function(context) {
	const { name } = context.query;

	let pokemon = await fetch(`https://pokeapi.co/api/v2/pokemon/${name}`);
	pokemon = await pokemon.json();

	let next = await fetch(`https://pokeapi.co/api/v2/pokemon/${pokemon.id + 1}`);
	next = await next.json();

	console.log(pokemon.id);

	return { pokemon, next_name: next.name };
}

export default Pokemon;
