// @flow strict
import React, { useState, useEffect } from "react";

// $FlowIgnore
import fetch from 'isomorphic-unfetch';

// $FlowIgnore
import styled from 'styled-components';

// $FlowIgnore
import Link from 'next/link';

// Components
import { Card } from '../components/Card';
import { Column } from '../components/Column';
import { Grid } from '../components/Grid';

// Pokemon
const Pokemon = () => {
	const [page, setPage] = useState('https://pokeapi.co/api/v2/pokemon');

	// useEffect
	useEffect((url) => {
		fetch(url)
			.then(res => {
				console.log(res);
				res.json()
			})
			.then(pokemon => {
				console.log(pokemon.results);
				setPage(pokemon.results);
			})
	}, [page]);

	return (
		<React.Fragment>
			<Grid>
				{!!page && page.map((pokemon) => (
					<Column key={pokemon.name} span="4">
						<Card>
							<Link href={`/pokemon/${pokemon.name}`} as={`/pokemon/${pokemon.name}`}>
								<a>{pokemon.name}</a>
							</Link>
						</Card>
					</Column>
				))}

				<button onClick={() => setPage('https://pokeapi.co/api/v2/pokemon')}>Load</button>
			</Grid>
		</React.Fragment>
	)
}

// Export
export default Pokemon;
