// @flow strict
import React, { useState, useEffect } from "react";

// $FlowIgnore
import fetch from 'isomorphic-unfetch';

// $FlowIgnore
import styled from 'styled-components';

// $FlowIgnore
import Link from 'next/link';

// Components
import { Card } from '../components/Card';
import { Column } from '../components/Column';
import { Grid } from '../components/Grid';

// Pokemon
const Pokemon = () => {
	const [data, setData] = useState({ results: [] });
	const [next, setNext] = useState();
	const [previous, setPrevious] = useState();
	const [query, setQuery] = useState('https://pokeapi.co/api/v2/pokemon');

	// useEffect
	useEffect(() => {
		const fetchData = async () => {
			const pokemon = await fetch(query)
				.then(res => res.json());

			console.log(pokemon.results);

			setData(pokemon.results);
			setNext(pokemon.next);
			setPrevious(pokemon.previous);

			console.log(data);
		};

		fetchData();
	}, [query]);

	return (
		<React.Fragment>
			<Grid>
				{data.map((pokemon) => (
					<Column key={pokemon.name} span="4">
						<Card>
							<Link href={`/pokemon/${pokemon.name}`} as={`/pokemon/${pokemon.name}`}>
								<a>{pokemon.name}</a>
							</Link>
						</Card>
					</Column>
				))}

				{!!next && <button onClick={() => setQuery(next)}>Next</button>}
				{!!previous && <button onClick={() => setQuery(previous)}>Previous</button>}
			</Grid>
		</React.Fragment>
	)
}

// Export
export default Pokemon;
