// @flow strict

// $FlowIgnore
import fetch from 'isomorphic-unfetch';

// $FlowIgnore
import styled from 'styled-components';

// $FlowIgnore
import Link from 'next/link';

// Components
import { Card } from '../components/Card';
import { Column } from '../components/Column';
import { Grid } from '../components/Grid';

// Flow
type Props = {
	next: string,
	previous: string,
	results: Array<{
		id: number,
		name: string
	}>
} 

// Pokemon
const Pokemon = ({ next, previous, results }: Props) => {
	let next_page;
	let previous_page;

	next ? next_page = next.replace('https://pokeapi.co/api/v2/pokemon', next) : null;
	previous ? previous_page = previous.replace('https://pokeapi.co/api/v2/pokemon', previous) : null;

	return (
		<React.Fragment>
			<Grid>
				{!!results && results.map((pokemon) => (
					<Column key={pokemon.name} span="4">
						<Card>
							<Link href={`/pokemon/${pokemon.name}`} as={`/pokemon/${pokemon.name}`}>
								<a>{pokemon.name}</a>
							</Link>
						</Card>
					</Column>
				))}
			</Grid>

			{!!next && <a href={next_page}>Next</a>}

			{!!previous && <a href={previous_page}>Next</a>}
		</React.Fragment>
	)
}

// Props
Pokemon.getInitialProps = async function() {
	const pokemon = await fetch(`https://pokeapi.co/api/v2/pokemon`)
		.then(res => res.json());

	return { next: pokemon.next, previous: pokemon.previous, results: pokemon.results };
}

// Export
export default Pokemon;
